mapper.select:

		SELECT NUM, NO, TITLE, WRITER, REGDATE
		FROM (
    		SELECT /*+ INDEX_DESC(board PK_BOARD) */ 
         		ROWNUM NUM, NO, TITLE, WRITER, REGDATE
    		FROM board
    		WHERE ROWNUM <= #{page} * #{amount}
		)
		WHERE NUM > (#{page} - 1) * #{amount}

mapper.update:
UPDATE board
SET title=#{title}, content=#{content}, url=#{url}
WHERE no = #{no}
